import java.util.*;

public class bear_and_three_musketeers
{
    static int n, m, mr = Integer.MAX_VALUE;

    static int[] r;

    static int[][] k1;

    static boolean[][] k2;

    public static void main( String[] args )
    {
        Scanner in = new Scanner( System.in );
        n = in.nextInt();
        m = in.nextInt();
        r = new int[n];
        k1 = new int[m][2];
        k2 = new boolean[n][n];
        for ( int i = 0; i < m; i++ )
        {
            int a = in.nextInt() - 1;
            int b = in.nextInt() - 1;
            r[a]++;
            r[b]++;
            k1[i][0] = a;
            k1[i][1] = b;
            k2[a][b] = true;
            k2[b][a] = true;
        }
        for ( int i = 0; i < m; i++ )
            for ( int j = 0; j < n; j++ )
                if ( k2[k1[i][0]][j] && k2[k1[i][1]][j]
                    && r[k1[i][0]] + r[k1[i][1]] + r[j] < mr )
                    mr = r[k1[i][0]] + r[k1[i][1]] + r[j];
        if ( mr != Integer.MAX_VALUE )
            System.out.println( mr - 6 );
        else
            System.out.println( -1 );
        in.close();
        System.exit( 0 );
    }
}

