typedef long long int ll;

ll scan(){ll x; scanf("%lld",&x); return x;}

void merge (ll *a, ll n, ll m) {
    ll i, j, k;
    ll x[n];
    for (i = 0, j = m, k = 0; k < n; k++) {
        x[k] = j == n      ? a[i++]
        : i == m      ? a[j++]
        : a[j] < a[i] ? a[j++]
        :               a[i++];
    }
    for (i = 0; i < n; i++) {
        a[i] = x[i];
    }
}

void merge_sort (ll *a, ll n) {
    if (n < 2)
        return;
    ll m = n / 2;
    merge_sort(a, m);
    merge_sort(a + m, n - m);
    merge(a, n, m);
}

