  int temp;
  int tot=limak.votes;
  for(int i=1; i<N; i++){
   temp=input.nextInt();
   if(temp>=limak.votes){
    votes.add(new Cand(i, temp));
    tot+=temp;
   }
  }
  Collections.sort(votes);
  if(votes.get(votes.size()-1).equals(limak)){
   for(int i=0; i<votes.size(); i++){
    if(votes.get(i).votes<limak.votes){
     tot-=votes.remove(i).votes;
     i--;
    }
   }
  }
  else{
   for(int i=0; i<votes.size(); i++){
    if(votes.get(i).votes<=limak.votes){
     tot-=votes.remove(i).votes;
     i--;
    }
   }
  }
  double avg=(double)tot/votes.size();
  if(votes.size()==1){
   System.out.println(0);
  }
  else if(avg-tot/votes.size()==0) System.out.println((int)avg-limak.votes+1);
  else System.out.println((int)Math.ceil(avg)-limak.votes);
 }
}
class Cand implements Comparable<Cand>{
 int votes;
 int ID;
 public Cand(int a, int b){
  ID=a;
  votes=b;
 }
 public boolean equals(Cand a){
  return ID==a.ID;
 }
 public int compareTo(Cand a){
  return votes-a.votes;
 }
 public String toString(){
  return votes+"";
 }
}

