using namespace std;

int main()
{
    int n, m;
    cin >> n >> m;
    vector<vector<int>> p(n);
    for (int i = 0; i < m; ++i) {
        int x, y;
        cin >> x >> y;
        --x;
        --y;
        p[x].push_back(y);
        p[y].push_back(x);
    }
    int answer = 1000 * 1000 * 1000;
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < p[i].size(); ++j) {
            for (int k = 0; k < p[i].size(); ++k) {
                if (k == j) continue;
                bool in = false;
                for (int z = 0; z < p[p[i][j]].size(); ++z) {
                    if (p[p[i][j]][z] == p[i][k]) {
                        in = true;
                        break;
                    }
                }
                if (!in) continue;
                answer = min(answer, (int)(p[i].size() + p[p[i][j]].size() + p[p[i][k]].size() - 6));
            }
        }
    }
    cout << ((answer == 1000 * 1000 * 1000) ? -1 : answer);
    return 0;
}

