    int temp;
    for(int i = 1;i<n;i++)
    {
        scanf("%d", &temp);
        v.push_back(temp);
    }

    sort(v.rbegin(), v.rend());

    int candies = 0;

    while(true)
    {
        if(limak > v[0])
            break;

        limak++;
        v[0]--;

        candies++;
        if(v[0] < v[1])
        {
            temp = v[0];
            int i = 1;

            while(temp < v[i])
            {
                v[i-1] = v[i];
                i++;   
            }
            v[i - 1] = temp;
        }
    }

    printf("%d\n", candies);


}

