#define lp(i, n) for(int i = 0; i < n; i++)
const int INF = 123456789;

int n, m;
vi graph[100010];
int ans = INF;

int main() {
    cin >> n >> m;
    lp(i, m) {
        int u, v;
        cin >> u >> v;
        u--; v--;
        graph[u].push_back(v);
        graph[v].push_back(u);
    }
    lp(i, n) {
        if (graph[i].size() < 2) continue;
        int len = graph[i].size();
        int x1, x2;
        x1 = x2 = INF;
        //cnt = 0;
        lp(j, len) {
            int v = graph[i][j];
            if (graph[v].size() < 2) continue;
            int deg = graph[v].size();
            if (deg < x1) {
                swap(x1, x2);
                x1 = deg;
            } else if (deg < x2) {
                x2 = deg;
            }
        }
        ans = min(ans, x1 + x2 + len - 6);
    }
    if (ans >= INF) cout << "-1" << endl;
    else cout << ans << endl;
}

