using namespace std;

int n , m;
int a[4008][4008];
int nei[4008];

struct pr {
    int a, b;

    pr ( int _a , int _b ) {
        a = _a;
        b = _b;
    }
};

vector < pr > vec;

void read () {
    cin >> n >> m;
    int u , v;
    for ( int i = 0; i < m; ++i ) {
        scanf ( "%d%d", &u , &v );
        vec.push_back(  pr ( u , v ) );
        a[u][v] = a[v][u] = 1;
        nei[u]++;nei[v]++;
    }
}

void solve () {

    int ans = 100000;
    int u , v;
    for ( int i = 0; i < m; ++i ){
        u = vec[i].a;
        v = vec[i].b;

        for ( int j = 1; j <= n; ++j )
            if ( a[u][j] && a[v][j] ) {
                ans = min ( ans , ( nei[u] + nei[v] + nei[j] - 6)  );
            }
    }

    if ( ans == 100000) cout << -1 << endl;
    else
    cout << ans << endl;
}

int main () {

    read ();
    solve ();
    return 0;
}

