const int MAXN = 5000;
const ll BOTTOM = -2123456789LL;

using namespace std;

vector<int> a[MAXN];
vector<pair<int, int> > q;

int main()
{
    #ifndef ONLINE_JUDGE 
        freopen("input.txt", "r", stdin);
    #endif //DEBUG

    int n, m;
    scanf("%d %d\n", &n, &m);

    for (int i = 0; i < m; i++) 
    {
        int x, y;
        scanf("%d %d\n", &x, &y);
        a[x].push_back(y);
        a[y].push_back(x);

        q.emplace_back(x, y);
    }

    ll ans = md;

    for (int i = 0; i < (int)q.size(); i++) 
    {
        int f = q[i].first;
        int s = q[i].second;
        vector<int> common;

        for (int j = 0; j < (int)a[f].size(); j++) 
        {
            common.push_back(a[f][j]);
        }

        for (int j = 0; j < (int)a[s].size(); j++) 
        {
            common.push_back(a[s][j]);
        }

        int preans = (int)a[f].size() + (int)a[s].size() - 4;

        sort(common.begin(), common.end());
        for (int j = 0; j < (int)common.size(); j++) 
        {
            if (j != (int)common.size() - 1) 
            {
                if (common[j] == common[j + 1]) 
                {
                    ans = min(ans, (preans + (int)a[common[j]].size() - 2) * 1LL);
                }
            }
        }
    }

    if (ans == md) 
    {
        printf("-1\n");
    } else 
    {
        printf("%lld\n", ans);
    }

    fprintf(stderr, "Execution time = %.4lfsec", (double)clock() / (double)CLOCKS_PER_SEC);    
    return 0;
}

