import java.util.HashSet;
import java.util.Scanner;


public class B {

 /*

5 6
1 2
1 3
2 3
2 4
3 4
4 5

7 4
2 1
3 6
5 1
1 7

  */

 @SuppressWarnings("unchecked")
 public static void main(String[] args) {
  Scanner reader = new Scanner(System.in);
  int n = reader.nextInt();
  int m = reader.nextInt();
  HashSet<Integer>[] g = new HashSet[n];
  for(int i = 0; i < n; i++) {
   g[i] = new HashSet<Integer>();
  }
  for(int i = 0; i < m; i++) {
   int o = reader.nextInt() - 1;
   int d = reader.nextInt() - 1;
   g[o].add(d);
   g[d].add(o);
  }
  int min = Integer.MAX_VALUE;
  for(int i = 0; i < n; i++) {
   for(int j : g[i]) {
    if(i < j) {
     if(g[i].size() < g[j].size()) {
      for(int k : g[i]) {
       if(g[j].contains(k)) {
        // i,j,k form a triangle
        min = Math.min(min, g[i].size() + g[j].size() + g[k].size() - 6);
       }
      }
     } else {
      for(int k : g[j]) {
       if(g[i].contains(k)) {
        // i,j,k form a triangle
        min = Math.min(min, g[i].size() + g[j].size() + g[k].size() - 6);
       }
      }
     }
    }
   }
  }
  System.out.println(min == Integer.MAX_VALUE ? -1 : min);
  reader.close();
 }
}

