int main()
{
    cin >> n >> m;
    for (int i=1; i<=m; i++)
    {
        int x,y;
        cin >> x >> y;
        adj[x].push_back(y); adj[y].push_back(x);
            c[x][y]=1;c[y][x]=1;
    }
    bool fl = false;
    int ans = 1000000;
    for (int i=1; i<=n; i++)
    {
        int r = adj[i].size() - 2;
        if (r >=0)
        for (int j=0; j<adj[i].size();j++)
        {
            int t = adj[j].size() - 2;
            if (t>=0)
            for (int k=0; k<t+2;k++)
            {
                if (k != i && c[k][i] == 1)
                {
                    int q = adj[k].size() - 2;
                    if (q >=0)
                    {
                    if (k + q + r < ans) ans = k + q + r;
                    fl = true;
                    }
                }
            }
        }
    }
    if (fl) cout << ans;
    else cout << -1;
    return 0;
}

