import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.HashSet;
import java.util.StringTokenizer;
import java.io.BufferedReader;
import java.io.InputStream;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskB solver = new TaskB();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskB {
        public void solve(int testNumber, InputReader in, PrintWriter out) {
            ArrayList<HashSet<Integer>> vek = new ArrayList<>();
            int n = in.nextInt();
            ++n;
            int m = in.nextInt();

            for (int i = 0; i < n; ++i) {
                vek.add(new HashSet<Integer>());
            }

            for (int i = 0; i < m; ++i) {
                int a = in.nextInt();
                int b = in.nextInt();
                vek.get(a).add(b);
                vek.get(b).add(a);
            }

            long maxcard = Long.MAX_VALUE;

            for (int i = 1; i < n; ++i) {
                for (Integer a : vek.get(i)) {
                    HashSet<Integer> first = vek.get(i);
                    HashSet<Integer> second = vek.get(a);

                    if (second.size() < first.size()) {
                        HashSet<Integer> temp = first;
                        first = second;
                        second = temp;
                    }

                    for (Integer b : first) {
                        if (second.contains(b) && b != i && b != a) {
                            maxcard = Math.min(maxcard, first.size() - 2 + second.size() - 2 + vek.get(b).size() - 2);
                        }
                    }
                }
            }

            if (maxcard != Long.MAX_VALUE) out.println(maxcard);
            else out.println(-1);

        }

    }

    static class InputReader {
        public BufferedReader reader;
        public StringTokenizer tokenizer;

        public InputReader(InputStream stream) {
            reader = new BufferedReader(new InputStreamReader(stream), 32768);
            tokenizer = null;
        }

        public String next() {
            while (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    tokenizer = new StringTokenizer(reader.readLine());
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

    }
}

