    for(int j = 1; j <= n; j++) {
      if(!s[i][j]) {
        continue;
      }
      if(edge[i].size() < edge[j].size()) {
        for(auto it : edge[i]) {
          if(s[it][j]) {
            ans = min(ans, (int)edge[i].size() + (int)edge[j].size() + (int)edge[it].size() - 6);
          }
