import java.io.*;
import java.util.*;
public class Rnd318B {
    BufferedReader br;
    PrintWriter out;
    StringTokenizer st;
    boolean eof;
    void solve() throws IOException {
        int n = nextInt();
        int m = nextInt();
        List<Set<Integer>> graph = new ArrayList<>();
        for(int i = 0; i < n; i++)
            graph.add(new HashSet<Integer>());
        int[] deg = new int[n];
        for(int i = 0; i < m; i++) {
            int a = nextInt() - 1;
            int b = nextInt() - 1;
            deg[a]++;
            deg[b]++;
            graph.get(a).add(b);
            graph.get(b).add(a);
        }
        int res = Integer.MAX_VALUE;
        for(int i = 0; i < n; i++) {
            for(int j : graph.get(i)) {
                for(int k : graph.get(j)) {
                    if(k != i && graph.get(k).contains(i)) {
                        int sum = deg[i] + deg[j] + deg[k] - 6;
                        res = Math.min(res, sum);
                    }
                }
            }
        }
        if(res != Integer.MAX_VALUE)
            System.out.print(res);
        else
            System.out.print(-1);

    }
    public Rnd318B() throws IOException {
        br = new BufferedReader(new InputStreamReader(System.in));
        out = new PrintWriter(System.out);
        solve();
        out.close();
    }
    public static void main(String[] args) throws IOException {
        new Rnd318B();
    }
    public String nextToken() {
        while (st == null || !st.hasMoreTokens()) {
            try {
                st = new StringTokenizer(br.readLine());
            } catch (Exception e) {
                eof = true;
                return null;
            }
        }
        return st.nextToken();
    }
    public String nextString() {
        try {
            return br.readLine();
        } catch (IOException e) {
            eof = true;
            return null;
        }
    }
    public int nextInt() throws IOException {
        return Integer.parseInt(nextToken());
    }
    public long nextLong() throws IOException {
        return Long.parseLong(nextToken());
    }
    public double nextDouble() throws IOException {
        return Double.parseDouble(nextToken());
    }
}

#include <bits/stdc++.h>
using namespace std;
const long long inf = 1000000000000;
bool kno[4004][4004];
vector<int>arr[4004];
int cnt[4004];



int main() {
    int n, m;
//  cin >> n >>m;
    scanf("%d %d", &n, &m);
    for(int i = 0;i < m;i++) {
        int a, b;
//      cin >> a >> b;
        scanf("%d %d", &a, &b);
        a--, b--;
        kno[a][b] = kno[b][a] = 1;
        arr[a].push_back(b);
        arr[b].push_back(a);
        cnt[a]++;
        cnt[b]++;
    }
    long long ans = inf;
    bool found = 0;
    for(int i = 0;i < n;i++) {
        for(int j = 0;j < arr[i].size();j++) {
            for(int k = 0;k < arr[arr[i][j]].size();k++) {
                int c = arr[arr[i][j]][k], b = arr[i][j], a = i;
                if(kno[i][c] and (a != b and b != c and c != a)) {
                    found = 1;
                    ans = min(ans, (long long)(cnt[a] - kno[a][b] - kno[a][c] + cnt[b] - kno[b][a] - kno[b][c] + cnt[c] - kno[c][a] - kno[c][b]));
                }
            }
        }
    }
    if(!found)
        puts("-1");
    else
        printf("%d", (int)ans);
    return 0;
}

