using namespace std ;
const int MX = 5000 ;
int n , m ; 
vector < int > adj[MX] ;
int ans = 1000000; 
int d[5000] ;
bool mark[MX] ;
int par[MX] ;
void DFS ( int v ) { 
 mark[v] = true ; 
 for ( int i = 0 ; i < (int) adj[v].size() ; i++ ) { 
  if ( ! mark[adj[v][i]] ) {
   par[adj[v][i]]= v ;
   DFS ( adj[v][i] ) ; 
  }
  else if ( adj[v][i] == par[par[v]] && par[par[v]] != par[v] ) {
   ans = min ( ans , d[v] + d[par[v]] + d[par[par[v]]] ) ; 
  }
 }
return ;
} 

