#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
vector <int> v[10004];
int g=100000008;
void stn(int u)
{
    for (int i=0;i<v[u].size();i++)
    {
        int w=v[u][i];
        for (int j=0;j<v[w].size();j++)
        {
            int y=v[w][j];
            for (int k=0;k<v[y].size();k++)
            {
                int z=v[y][k];
                if (z==u)
                {
                    int x=v[u].size()+v[w].size()+v[y].size()-6;
                    if (x<g)
                    {
                        g=x;
                    }
                }
            }
        }
    }
    return;
}
int main()
{
    ios_base::sync_with_stdio(0);cin.tie(0);
    ll n,m;cin>>n>>m;
    for (int i=0;i<m;i++)
    {
        int a,b;cin>>a>>b;
        a--;b--;
        v[a].push_back(b);
        v[b].push_back(a);
    }
    for (int i=0;i<n;i++)
        stn(i);
    if (g!=100000008)
        cout<<g;
    else
        cout<<-1;
    return 0;
}
