int main(){
    int ans = -1;
    int V, E; cin >> V >> E;
    map<int, int> degrees;
    vector<int> graph[V];
    for (int r = 0; r < E; r++){
        int a, b; cin >> a >> b;
        a--; b--;
        graph[a].push_back(b);
        graph[b].push_back(a);
        degrees[a]++;
        degrees[b]++;
    }
    for (int r = 0; r < V; r++){
        sort(graph[r].begin(), graph[r].end());
    }
    int a, b;

    for (int r = 0; r < V; r++){
        for (int k:graph[r]){
            a = 0; b = 0;
            while (a != graph[r].size() and b != graph[k].size()){
                int s = graph[r][a]; int t = graph[k][b];
                if (s < t){
                    a++;
                }
                if (s > t){
                    b++;
                }
                if (s == t){
                    if (ans == -1 || ans > degrees[r]+degrees[k]+degrees[s]-6){
                        ans = degrees[r]+degrees[k]+degrees[s]-6;
                    }
                    a++; b++;
                }
            }
        }
    }
    cout << ans << endl;

return 0;
}

