#include<bits/stdc++.h>
#define sz 5555
#define ll long long
#define inf (ll)1<<60
using namespace std;

int n ,m,pivot,marker[sz];
vector<int>edges[sz];

ll f(int pos,int height){
//cout<<"pos:"<<pos<<endl;
      if(height==3 ){
            if(pivot==pos)return 0;
            return inf;
      }
      ll sum=inf;
      for(int i = 0,len=edges[pos].size() ; i <len;i++ ){
            int v= edges[pos][i];
            if(marker[v]==pivot)continue;
            ll tmp=f(v,height+1);
            marker[v]=pivot;
            if(tmp!=inf){
                  tmp+=(len-2);

                  sum=min(sum,tmp);
            }
      }
      return sum;

}

int main(){
     // freopen("input.txt","r",stdin);
      //cin>>n>>m;
      scanf("%d %d",&n,&m);
      for(int i = 0 ; i <=n;i++)edges[i].clear();

      ll ans = inf;
      memset(marker,0,sizeof(marker));


      for(int i =0,u,v;i<m;i++){
            //cin>>u>>v;
            scanf("%d %d",&u,&v);
            edges[u].push_back(v);
            edges[v].push_back(u);
      }

      for(int i =1;i<=n;i++){
            pivot=i;
            //cout<<"pivot:"<<pivot<<endl;
            ans=min(ans,f(i,0));
      }
      if(ans==inf)cout<<"-1\n";
      else cout<<ans<<"\n";

      return 0;
}

