int recognition[4005];
bool knows[4005][4005];
int main()
{
    int n, m; cin >> n >> m;
    int first, second; 
    for (int i = 0; i < n; i++)
    {
        cin >> first >> second;
        knows[first][second] = true;
        knows[second][first] = true;
        recognition[first] ++, recognition[second] ++;
    }
    int one =0, two = 0, three = 0;
    int minsum = INT_MAX;
    for (int i = 0; i < n; i++)
    {
        for (int j = i + 1; j < n; j++)
        {
            for (int k = j + 1; k < n; k++)
            {
                if (!knows[i][j] || !knows[i][k] || !knows[j][k]) continue;
                int cur = recognition[i] + recognition[j] + recognition[k] - 6;
                if (cur < minsum)
                {
                    minsum = cur;
                    one = i, two = j, three = k;
                }
            }
        }
    }
    if(!one && !two) cout << -1, exit(0);
    cout << minsum << endl;
}

