/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 *
 * @author Morgrey
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        OutputWriter out = new OutputWriter(outputStream);
        TaskB solver = new TaskB();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskB {
        List<List<Integer>> g;

        public void solve(int testNumber, InputReader in, OutputWriter out) {
            g = new ArrayList<>();
            int n = in.readInt(), m = in.readInt();
            for (int i = 0; i < n; i++) {
                g.add(new ArrayList<>());
            }
            for (int i = 0; i < m; i++) {
                int x = in.readInt() - 1;
                int y = in.readInt() - 1;
                g.get(x).add(y);
                g.get(y).add(x);
            }
            int one = -1;
            int second = -1;
            int third = -1;
            long max_d_o = Integer.MAX_VALUE;
            long max_d_s = Integer.MAX_VALUE;
            long max_d_t = Integer.MAX_VALUE;
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    for (int z = 0; z < n; z++) {
                        if (i != j && j != z && z != i) {
                            if (g.get(i).contains(j) && g.get(i).contains(z) && g.get(j).contains(z)) {
                                if (max_d_o + max_d_s + max_d_t > g.get(i).size() + g.get(j).size() + g.get(z).size()) {
                                    one = i;
                                    second = j;
                                    third = z;
                                    max_d_o = g.get(i).size();
                                    max_d_s = g.get(j).size();
                                    max_d_t = g.get(z).size();
                                }
                            }
                        }
                    }
                }
            }
            if (one != -1 && second != -1 && third != -1) {
                out.printLine(max_d_o + max_d_s + max_d_t - 6);
            } else
                out.print(-1);
        }

    }

    static class OutputWriter {
        private final PrintWriter writer;

        public OutputWriter(OutputStream outputStream) {
            writer = new PrintWriter(new BufferedWriter(new OutputStreamWriter(outputStream)));
        }

        public OutputWriter(Writer writer) {
            this.writer = new PrintWriter(writer);
        }

        public void close() {
            writer.close();
        }

        public void printLine(long i) {
            writer.println(i);
        }

        public void print(int i) {
            writer.print(i);
        }

    }

    static class InputReader {
        private InputStream stream;
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;
        private SpaceCharFilter filter;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public int read() {
            if (numChars == -1)
                throw new InputMismatchException();
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (numChars <= 0)
                    return -1;
            }
            return buf[curChar++];
        }

        public int readInt() {
            int c = read();
            while (isSpaceChar(c))
                c = read();
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                if (c < '0' || c > '9')
                    throw new InputMismatchException();
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public boolean isSpaceChar(int c) {
            if (filter != null)
                return filter.isSpaceChar(c);
            return isWhitespace(c);
        }

        public static boolean isWhitespace(int c) {
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        public interface SpaceCharFilter {
            public boolean isSpaceChar(int ch);

        }

    }
}

