vector < set <int> > graph;

int min_cost(int mid) {
    int cost = -1;
    for(set<int>::iterator i = graph[mid].begin(); i != graph[mid].end(); i++) {
        for (set<int>::iterator j = graph[mid].begin(); j != graph[mid].end(); j++) {
            if (*(graph[*i].lower_bound(*j)) == *j) {
                int cur = graph[mid].size() + graph[*i].size() + graph[*j].size() - 6;
                if (cost == -1)
                    cost = cur;
                else
                    cost = min(cur,cost);
            }

        }
    }
    return cost;
}

