int find_root(int x) {
    if (p[x] == x)
        return x;
    return p[x] = find_root(p[x]);
}

void unite(int x, int y) {
    x = find_root(x);
    y = find_root(y);
    if (root_rank[x] < root_rank[y])
        p[x] = y;
    else {
        p[y] = x;
        if (root_rank[x] == root_rank[y]) {
            ++root_rank[y];
        }
    }
