int main(){
    int n,m;
    cin>>n>>m;
    int count[n];
    REP(i,n) count[i]=0;
    set<pii> roads;
    REP(i,m){
        int a,b;
        cin>>a>>b;
        a--;
        b--;
        if(a>b) swap(a,b);
        roads.insert(pii(a,b));
        count[a]++;
        count[b]++;
    }
    // REP(i,n) cout << count[i] << " " ;
    // cout << endl;
    int ans = INT_MAX;
    EACH(it, roads){
        int a = it->first, b = it->second;
        FOR(c,max(a,b)+1, n){
            if(a!=c && b!=c && EXIST(roads,pii(a,c)) && EXIST(roads,pii(b,c))){
                // cout << a << " " << b << " " << c << " " <<
                //      count[a]+count[b]+count[c]-6 << endl;
                ans=min(ans, count[a]+count[b]+count[c]-6);
            }
        }
    }
    if(ans==INT_MAX) ans = -1;
    cout << ans << endl;
}
