using namespace std;

int n, m;
vector<int> men[4001];
int matr[4001][4001];

int dfs(int cur, int last) {
 int sum = 1e9;

 for(int i = 0; i < men[cur].size(); i++) {
  if(men[cur][i] == last) continue;
  if(last == 0) {
   int temp = dfs(men[cur][i], cur);
   if(temp != -1) sum = temp;
  } else {
   if(matr[men[cur][i]][last] > 0 && men[men[cur][i]].size() - 2 < sum) {
    sum = men[men[cur][i]].size() - 2;
   }
  }
 }

 if(sum == 1e9) return -1;
 else return sum + men[cur].size() - 2;
}

int main() {
 ios_base::sync_with_stdio(false);
 // freopen("input.txt", "r", stdin);
 // freopen("output.txt", "w", stdout);

 cin >> n >> m;

 int a, b;
 for (int i = 0; i < m; i++) {
  cin >> a >> b;
  matr[a][b]++;
  matr[b][a]++;
  men[a].push_back(b);
  men[b].push_back(a);
 }


 int min = 1e9;
 for (int i = 1; i < n + 1; i++) {
  int temp = dfs(i, 0);
  if(temp != -1 && temp < min) min = temp;
 }

 if(min == 1e9) cout << -1;
 else cout << min;

 return 0;
}

