public class Main
{

 public static int maximo(int [] v){
  int maxi = 0; int max_id = -1;
  for(int i = 1; i < v.length; i++){
   if(v[i] > maxi){ 
    maxi = v[i];
    max_id = i;
   }
  }
  return max_id;
 }
 public static void main(String args[]){
  Scanner scan = new Scanner(System.in);
  int n = scan.nextInt();
  int [] v = new int[n];

  int maxi = 0; int max_id = -1;
  for(int i = 0; i < n; i++){
   v[i] = scan.nextInt();
   if(v[i] > maxi){ maxi = v[i]; max_id = i;}
  }

  int ans = 0;
  while(max_id != 0 && v[0] <= v[max_id]){
   v[0] ++;
   v[max_id]--;
   max_id = maximo(v);
   ans ++;
  }
  System.out.println(ans);
 }
}
