#include<iostream>
#include<cmath>
using namespace std;

void heapify(int *a,int i,int end){
 int l=(i<<1)+1;
 int r=l+1;
 int root=i;
 if(l<end&&a[l]>a[root])
  root=l;
 if(r<end&&a[r]>a[root])
  root=r;
 if(root!=i){
  int temp=a[i];
  a[i]=a[root];
  a[root]=temp;
  heapify(a,root,end);
 }
}

void buildHeap(int *a,int end){
 for(int i=end>>1;i>=0;i--)
  heapify(a,i,end);
}

int main(){
 int n;
 cin>>n;
 int a[n-1];
 int li;
 cin>>li;
 for(int i=0;i<n-1;i++)
  cin>>a[i];
 buildHeap(a,n-1);
 int bribe=0;
 //cout<<"fuck\n";
 while(li<=a[0]){
  int dif=((a[0]-li)>>1)+1;
  int lchild=a[1]-1;
  int rchild=a[2]-1;
  int minChild=min(a[0]-lchild,a[0]-rchild);
  int actDif=min(dif,minChild);
  bribe+=actDif;
  a[0]-=actDif;
  li+=actDif;
  heapify(a,0,n-1);
  //cout<<"fuck heap\n";
 }
 cout<<bribe<<"\n";
 return 0;
}

