void selection_sort(int n, int data[])
{
 int idx;
 int min;
 for (int i = 0; i < n - 1; i++)
 {
  idx = i;
  min = data[idx];
  for (int j = i; j < n; j++)
  {
   if (data[j] < min)
   {
    idx = j;
    min = data[j];
   }
  }
  std::swap(data[idx], data[i]);
 }
}

