import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

public class Campers {

    public static int getNumberOfMax(List<Integer> list, int max) {
        int count = 0;
        for (Integer integer : list) {
            if (integer == max) {
                count++;
            }
        }
        return count;
    }

    public static void main(String[] args) throws IOException {

        BufferedReader reader = new BufferedReader(new InputStreamReader(
                System.in));
        int x = Integer.valueOf(reader.readLine());
        ArrayList<Integer> list = new ArrayList<Integer>();
        for (String string : reader.readLine().split(" ")) {
            list.add(Integer.valueOf(string));
        }
        int testMax = list.get(0);
        boolean flag = true;
        for (int i = 1; i < list.size(); i++) {
            if (testMax <= list.get(i)) {
                flag = false;
                break;
            }
        }
        if (flag) {
            System.out.println(0);
            return;
        }
        Collections.sort(list);
        if (list.get(list.size() - 1) == list.get(0)) {
            int count = getNumberOfMax(list, list.get(list.size() - 1));
            System.out.println(count - 1);
            return;
        }
        int maxCount = getNumberOfMax(list, list.get(list.size() - 1));
        if (maxCount == 1) {
            System.out.println((maxCount + testMax) / 2 + 1);
        } else {
            int count = maxCount + 1;
            int division = (list.get(list.size() - 1) * maxCount) + list.get(0);
            int mustValue = (division / count);
            int res = 0;
            for (int i = 0; i < maxCount; i++) {
                res += (list.get(list.size() - 1) - mustValue);
            }
            System.out.println(res);
        }
    }
}

