using namespace std;

struct triple
{
 int x, y, z;
 triple(int xx, int yy, int zz)
 {
  x = xx;
  y = yy;
  z = zz;
 }
};

triple help(int a, int b, int x, int y)
{
 if (a == x)
  return triple(min(b, y), max(b, y), a);
 if (a == y)
  return triple(min(b, x), max(b, x), a);
 if (b == x)
  return triple(min(a, y), max(a, y), b);
 if (b == y)
  return triple(min(a, x), max(a, x), b);
 return triple(-1, -1, -1);
}

int main()
{
 //freopen("input.txt", "r", stdin);
 //freopen("output.txt", "w", stdout);

 int n, m;
 cin >> n >> m;
 vector<int> known(n);
 vector<bool> hash_table(n * n);
 vector<pair<int, int> > pr(m);
 for (int i = 0; i < m; i++)
 {
  int l, r;
  cin >> l >> r;
  l--, r--;
  known[l]++, known[r]++;
  if (l > r)
   swap(l, r);
  hash_table[l * n + r] = 1;
  pr[i] = make_pair(l, r);
 }
 int res = 999999;
 for (int i = 0; i < m; i++)
 {
  for (int j = i + 1; j < m; j++)
  {
   triple h = help(pr[i].first, pr[i].second, pr[j].first, pr[j].second);
   if (h.x != -1 && hash_table[h.x * n + h.y])
   {
    res = min(res, known[h.x] + known[h.y] + known[h.z]);
   }
  }
 }
 cout << (res != 999999 ? res - 6 : -1) << endl;
 return 0;
}

