public class B {
   public static void main(String[] args) {
      Scanner in = new Scanner(System.in);
      int n = in.nextInt(), m = in.nextInt();


      Warrior[] warriors = new Warrior[n];

      for(int i = 0; i < n; i++) {
         warriors[i] = new Warrior(i);
      }

      for(int i = 0; i < m; i++) {
         int warA = in.nextInt() - 1, warB = in.nextInt() - 1;
         warriors[warA].friends.add(warriors[warB]);
         warriors[warB].friends.add(warriors[warA]);

      }

      int best = -1;

      for(int i = 0; i < n; i++) {
         ArrayList<Warrior> neighbors = warriors[i].friends;
         for(int j = 0; j < neighbors.size(); j++) {
            for(int k = j + 1; k < neighbors.size(); k++) {
               Warrior f1 = neighbors.get(j);
               Warrior f2 = neighbors.get(k);
               if(f1.friends.contains(f2)) {
                  int fame = neighbors.size() + f2.friends.size() + f1.friends.size() - 6;  
                  //System.out.println(fame + ": " + (i+1) + " " + (f1.index+1) + " " + (f2.index+1));
                  if(best == - 1 || fame < best)
                     best = fame;
               }

            }
         }
      }

      System.out.println(best);

   }
}

class Warrior {
   public ArrayList<Warrior> friends;
