int transfer()
{
    int count=1;
    int n=x.size()-1;
    while(x[n]==x[n-1])
    {count++;n--;}

    n=x.size()-1;
    int remember=x[n];
    int flag=0;
    for(int i=0;i<count;i++)
    {
        if(x[0]>remember)
            break;
        {x[n--]--;x[0]++;flag++;}
    }
    return flag;
}
