using namespace std;

int main(int argc, char *argv[]) {
  ios::sync_with_stdio(false); cin.tie(NULL);
  int N, M;
  cin >> N >> M; // nodes, edges
  vector<pair<int, int>> edges;
  vector<unordered_set<int>> edgeList(N);
  for (int i = 0; i < M; ++i) { // loop though edges
    int a, b;
    cin >> a >> b;
    --a; --b;
    edges.emplace_back(a, b);
    edgeList[a].insert(b);
    edgeList[b].insert(a);
  }
  int minimumRecognitions = -1;
  for (pair<int, int> edge : edges) {
    // find third musketeer
    for (int c : edgeList[edge.first]) {
      if (edgeList[edge.second].count(c)) {
        int recognitions = edgeList[edge.first].size() + edgeList[edge.second].size() + edgeList[c].size() - 6;
        if (minimumRecognitions == -1 || recognitions < minimumRecognitions) {
          minimumRecognitions = recognitions;
        }
      }
    }
  }
  cout << minimumRecognitions << endl;
  return 0;  
}

