using namespace std;
#define MAXNUM 4005
#define BIG 1000000000
int main() {
 ios::sync_with_stdio(false);
 int nKnows[MAXNUM] = {};
 int friends[MAXNUM][MAXNUM] = {{0}};
 bool matrix[MAXNUM][MAXNUM] = {{false}};
 int n;
 int m;
 int a;
 int b;
 bool found = false;
 int ans = BIG;

 cin >> n >> m;
 for (int i = 0; i < m; ++i) {
  cin >> a >> b;
  a--;
  b--;
  matrix[a][b] = true;
  matrix[b][a] = true;
  friends[a][nKnows[a]] = b;
  friends[b][nKnows[b]] = a;
  nKnows[a]++;
  nKnows[b]++;
 }
 for (int i = 0; i < n; ++i) {
  if (nKnows[i] > 0) {
   for (int j = 0; j < nKnows[i]; ++j) {
    int temp = friends[i][j];
    if (temp > i) {
     for (int k = 0; k < nKnows[j]; ++k) {
      if (friends[temp][k] > temp) {
       if (matrix[i][friends[temp][k]] == true) {
        found = true;
        assert(matrix[i][friends[temp][k]] == true);
        assert(matrix[temp][friends[temp][k]] == true);
        assert(matrix[i][temp] == true);
        int tempans = nKnows[i] + nKnows[temp] + nKnows[friends[temp][k]] - 6;
        ans = min(ans, tempans);
       }
      }
     }
    }
   }
  }
 }
 if (found) {
  cout << ans << '\n';
 } else {
  cout << -1 << '\n';
 }
 return 0;
}

