template <class T> T gcd( T a, T b )
{
    return b == 0 ? a : gcd(b, a % b);
}
template <class T> T lcm ( T a, T b )
{
    return ( a / gcd ( a, b ) ) * b;
}
template <class T> T power ( T a, T p )
{
    T res = 1, x = a;
    while ( p )
    {
        if ( p & 1 ) res = res * x;
        x = x * x;
        p >>= 1;
    }
    return res;
}
template <class T> T cordinatlenth(T a, T b, T c, T d)
{
    return sqrt( sq(a - c) + sq(b - d) );
}
