lld bigmod ( lld a, lld p, lld mod )
{
    lld res = 1, x = a;
    while ( p )
    {
        if ( p & 1 ) res = ( res * x ) % mod;
        x = ( x * x ) % mod;
        p >>= 1;
    }
    return res;
}

