bool contains(set<int> s, int x) {
  return s.find(x) != s.end();
}

int main() {
  int n, m, a, b;
  cin >> n >> m;
  vector<set<int> > knowing (n+1, set<int>());

  for (int i = 0; i < m; i++) {
    cin >> a >> b;
    knowing.at(a).insert(b);
    knowing.at(b).insert(a);
  }

  int min_sum = INT_MAX;

  for (int i = 1; i <= n; i++) {
    set<int>::iterator it = knowing.at(i).lower_bound(i+1);
    while (it != knowing.at(i).end()) {
      set<int>::iterator it2 = it;
      it2++;

      while (it2 != knowing.at(i).end()) {
        if (contains(knowing.at(*it), *it2)) {
          min_sum = min(min_sum, int(knowing.at(i).size() + knowing.at(*it).size() + knowing.at(*it2).size() - 6));
        }
        it2++;
      }

      it++;
    }
  }

  // no triple found
  if (min_sum == INT_MAX) {
    min_sum = -1;
  }

  cout << min_sum << "\n";

  return 0;
}

