typedef set<int>::iterator Iter;

int main() {
  int n, m;
  int a[4000], b[4000];
  int min_sum = -1;
  set<int> nexts[4000];
  cin >> n >> m;
  for (int i = 0; i < m; ++i) {
    cin >> a[i] >> b[i];
  }
  for (int i = 0; i < n; ++i) {
    nexts[i].clear();
  }
  for (int i = 0; i < m; ++i) {
    nexts[a[i] - 1].insert(b[i] - 1);
    nexts[b[i] - 1].insert(a[i] - 1);
  }
  for (int i = 0; i < m; ++i) {
    for (Iter iter = nexts[a[i] - 1].begin(); iter != nexts[a[i] - 1].end(); ++iter) {
      if (nexts[b[i] - 1].count(*iter) > 0) {
        int s = nexts[a[i] - 1].size() + nexts[b[i] - 1].size() + nexts[*iter].size() - 6;
        if (min_sum < 0 or s < min_sum) {
          min_sum = s;
        }
      }
    }
  }
  cout << min_sum << endl;
  return 0;
}

