int main()
{
    int n , sum = 0 , cont = 1 , temp , ans , lim , mx = -1 ;

    sf1(n);
    sf1(lim);

    forln(i,0,n-1)
    {
        sf1(temp);
        mx = max(mx,temp);

        if(temp>=lim)
        {
            cont++;
            sum += (temp-lim);
        }
    }

    ans = floor(sum/cont) ;

    if(cont>1)
        ans += (sum%cont)>1?2:1 ;

    temp = lim + ans ;
    ans = max(ans,mx-temp);
    deb(ans);

    return 0 ;
}

