#include<bits/stdc++.h>
using namespace std;
struct node
{
    int val,ord;
    bool operator <(const node& rhs) const{
      return val<rhs.val;
    }
};
int main()
{
    int n,a[105];
    scanf("%d",&n);
    priority_queue<node> q;
    node e;
    for(int i=1;i<=n;i++)
    {
        scanf("%d",&a[i]);
        e.val=a[i],e.ord=i;
        q.push(e);
    }
    int ret=0,cnt=a[1];
    while(true)
    {
        node f=q.top();
        q.pop();
        if(f.ord==1) break;
        ret++;
        f.val--;
        node a;
        a.ord=1,a.val=cnt+ret;
        q.push(f);
        q.push(a);
    }
    printf("%d\n",ret);
    return 0;
}

