#include <stdio.h>

void mergeSort(int * arr, int l, int r) {
 if (l == r) {
  return;
 }
 if (l == r - 1) {
  if (arr[l] > arr[r]) {
   int tmp = arr[l];
   arr[l] = arr[r];
   arr[r] = tmp;
  }
  return;
 }

 int med = l + (r - l) / 2 + 1;

 mergeSort(arr, l, med - 1);
 mergeSort(arr, med, r);

 int * a = new int[r - l + 1];
 int offset = med;
 int i1 = 0;
 int i2 = 0;
 int c = 0;
 while(l + i2 < med && i1 + med <= r) {
  while(arr[med + i1] <= arr[l + i2] && i1 + med <= r)
   a[c++] = arr[med + i1++];
  while(arr[med + i1] >= arr[l + i2] && l + i2 < med)
   a[c++] = arr[l + i2++];
 }
 while(i1 + med <= r)
  a[c++] = arr[med + i1++];
 while(l + i2 < med)
  a[c++] = arr[l + i2++];
 for (int i = l; i <= r; i++) {
  arr[i] = a[i-l];
 }
}

