    vector<int> v;
    int a;

    for (int i = 0; i < n; i++) {
        int num;
        cin >> num;
        v.push_back(num);
        if (i == 0) a = num;
    }

    sort(v.begin(), v.end(), sort_pred());

    if (v[0] == a) cout << 0 << endl;

    else {

        int pos;
        for (int i = 0; i < n; i++) if (v[i] == a) {
            pos = i;
            break;
        }

        int count = 0; 
        while (pos > 0){

            if (v[pos] >= v[pos - 1]) {

                swap(v[pos], v[pos - 1]);
                pos--;
            }

            else {

                int s = v[pos - 1] - v[pos];
                count = (s % 2 == 0) ?count +  (s / 2) : count + s / 2 + 1;

                v[pos] += count;
                v[pos - 1] -= count;
                swap(v[pos], v[pos - 1]);
                pos--;


            }

        }

        cout << count << endl;
    }


    return 0;
}

