void sort(int* mas, int n)
{
 int tmp = 0;

 for(int i=0; i<n; i++) {
  for(int j=1; j<n; j++) {
   if(mas[j-1] < mas[j]) {
    tmp = mas[j];
    mas[j] = mas[j-1];
    mas[j-1] = tmp;
   }
  }

  n--;
 }
