class Solution {

    public void solve(int testno, InputReader in, PrintWriter out) {

        int n = in.readInt();
        PriorityQueue<Integer> myqueue = new PriorityQueue<Integer>(new Comparator<Integer>() {
            @Override
            public int compare(Integer o1, Integer o2) {
                return (o2 - o1);
            }
        });
        int first = 0;
        first = in.readInt();
        for (int i = 1; i < n; i++) {
            int get = in.readInt();
            myqueue.add(get);
        }
        int ans = 0;
        if (myqueue.isEmpty() == false && first > myqueue.peek()) {
            out.println(0);
        } else {
            while (myqueue.isEmpty() == false && first <= myqueue.peek()) {
                ans++;
                first++;
                int cnt = myqueue.peek();
                myqueue.remove();
                myqueue.add(cnt - 1);
            }
            out.println(ans);
        }
    }
}

class InputReader {

    private InputStream stream;
    private byte[] buf = new byte[1024];
    private int curChar;
    private int numChars;

    public InputReader(InputStream stream) {
        this.stream = stream;
    }

    public int read() {
        if (numChars == -1) {
            throw new InputMismatchException();
        }
        if (curChar >= numChars) {
            curChar = 0;
            try {
                numChars = stream.read(buf);
            } catch (IOException e) {
                throw new InputMismatchException();
            }
            if (numChars <= 0) {
                return -1;
            }
        }
        return buf[curChar++];
    }

    public int readInt() {
        int c = read();
        while (isSpaceChar(c)) {
            c = read();
        }
        int sgn = 1;
        if (c == '-') {
            sgn = -1;
            c = read();
        }
        int res = 0;
        do {
            if (c < '0' || c > '9') {
                throw new InputMismatchException();
            }
            res *= 10;
            res += c - '0';
            c = read();
        } while (!isSpaceChar(c));
        return res * sgn;
    }

    public String readLine() {
        int c = read();
        while (checkEndLine(c)) {
            c = read();
        }
        StringBuffer res = new StringBuffer();
        do {
            res.appendCodePoint(c);
            if (checkEndLine(c) == true) {
                break;
            }
            c = read();
        } while (!checkEndLine(c));
        return res.toString();
    }

    public String readString() {
        int c = read();
        while (isSpaceChar(c)) {
            c = read();
        }
        StringBuffer res = new StringBuffer();
        do {
            res.appendCodePoint(c);
            c = read();
        } while (!isSpaceChar(c));
        return res.toString();
    }

    public long readLong() {
        return Long.parseLong(readString());
    }

    public double readDouble() {
        return Double.parseDouble(readString());
    }

    public float readFloat() {
        return Float.parseFloat(readString());
    }

    public static boolean checkEndLine(int c) {
        return c == '\n' || c == -1 || c == '\r';
    }

    public static boolean isSpaceChar(int c) {
        return c == '\n' || c == '\r' || c == '\t' || c == -1 || c == ' ';
    }
}

