import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int candidates = sc.nextInt();
        int [] votes = new int[candidates];

        for (int i = 0 ; i < candidates ; ++i) {
            votes[i] = sc.nextInt();
        }

        int max = votes[0];
        int maxIndex = 0;
        for (int i = 0 ; i < candidates ; ++i) {
            if (votes[i] >= max) {
                max = votes[i];
                maxIndex = i;
            }
        }

        if (maxIndex == 0) {
            System.out.println("0");
            return;
        }

        int counter = 0;
        while (!isFirstMax(votes)) {
            int index = getCurrentMaxIndex(votes);
            votes[index] -= 1;
            votes[0] += 1;
            counter++;
        }

        System.out.println(counter);
    }

    private static boolean isFirstMax(int [] votes) {
        int max = votes[0];
        int maxIndex = 0;
        for (int i = 0 ; i < votes.length ; ++i) {
            if (votes[i] >= max) {
                max = votes[i];
                maxIndex = i;
            }
        }

        return (maxIndex == 0);
    }

    private static int getCurrentMaxIndex(int [] votes) {
        int max = votes[0];
        int maxIndex = 0;
        for (int i = 1 ; i < votes.length ; ++i) {
            if (votes[i] >= max) {
                max = votes[i];
                maxIndex = i;
            }
        }

        return maxIndex;
    }
}

