void dfs(int node, int dep){
 depth[node] = dep;
 stk[dep] = node;
 for(int i=0;i<adj[node].size();i++){
  int &x = adj[node][i];
  if(depth[x] > -1){
   if(dep-depth[x] == 2){
    ans = min(ans,(int(adj[node].size())-2)+(int(adj[stk[dep-1]].size())-2)+(int(adj[stk[dep-2]].size())-2));
   }
  }else{
   dfs(x, dep+1);
  }
 }

 depth[node] = -1;
}

