#define INF 1e9
#define SQ(c) ((c) * (c))
#define SZ(c) int(c.size())
#define ALL(c) (c.begin(), c.end())
#define SET(c, v) memset(c, v, sizeof (c))

const int N = 4e3 + 10;

bool g[N][N];
int in[N];
vector <vector <int>> adj;

int main() {
#ifndef ONLINE_JUDGE
 freopen("inp.txt", "r", stdin);
#endif
 int n, m, u, v;
 scanf("%d %d", &n, &m);
 adj.resize(n);
 for (int i = 0; i < m; ++i) {
  scanf("%d %d", &u, &v);
  --u, --v;
  g[u][v] = g[v][u] = 1;
  adj[u].push_back(v);
  adj[v].push_back(u);
  in[u]++, in[v]++;
 }
 int ans = INF;
 for (int i = 0; i < n; ++i) {
  for (int j = 0; j < SZ(adj[i]); ++j) {
   for (int k = j + 1; k < SZ(adj[i]); ++k) {
    if (g[adj[i][j]][adj[i][k]]) {
     ans = min(ans, in[i] + in[adj[i][j]] + in[adj[i][k]] - 6);
    }
   }
  }
 }
 if (ans == INF) puts("-1");
 else printf("%d\n", ans);
 return 0;
}

