int main(void)
{

    ios :: sync_with_stdio(false);
    cin >> n >> m;

    for (int i = 0; i < m; ++i)
    {
        cin >> x >> y;
        adj[x].push_back(y);
        adj[y].push_back(x);
        mat[x][y] = mat[y][x] = 1;
    }

    int menor = 0x3f3f3f3f;
    for (int i = 1; i <= n; ++i)
    {
        for (int j = 0; j < adj[i].size(); ++j)
        {
            for (int k = 0; k < adj[i].size(); ++k)
            {
                int u = adj[i][j];
                int v = adj[i][k];
                if (u == v)
                    continue;
                if (mat[u][v])
                {
                    menor = min(menor, (int)adj[u].size() - 2 + (int)adj[v].size() - 2 + (int)adj[i].size() - 2);
                }
            }
        }
    }

    if (menor == 0x3f3f3f3f)
    {
        cout << -1 << "\n";
    }
    else
    {
        cout << menor << "\n";
    }
    return 0;
}

