typedef long long int ll;
void qcksrt(ll a[],ll first,ll last)
{
 if(first<=last)
 {
        ll wall=first,pivot=first,i,temp;
        for(i=first;i<=last;i++)
        {
            if(a[i]<a[pivot])
            {
             wall++;
             temp=a[wall];
             a[wall]=a[i];
             a[i]=temp;
            }
        }
        temp=a[wall];
        a[wall]=a[pivot];
        a[pivot]=temp;
        qcksrt(a,first,wall-1);
        qcksrt(a,wall+1,last);
    }
}
