vector<vi> G;
int adj[5000][5000];
vi deg;
vi vis;
int ans = 0;
vi nodes;
void dfs(int start, int u , int d){

    vis[u] = 1;
    nodes.push_back(u);
//  printf("u = %d d = %d\n", u,d);
    if(d == 3 && vis[u] && adj[start][u]){
        int temp = 0;
        for (int j = 0; j < nodes.size(); ++j) {
//          printf("%d ", nodes[j]);
            int n = nodes[j];
            temp += deg[n] - 2 < 0 ? 0 : deg[n] - 2;
        }
//      printf("Debug: ans = %d temp = %d\n",ans, temp);
        ans = min(ans, temp);

    }
    for (int i = 0,v; i < G[u].size(); ++i) {
        v = G[u][i];

        if(!vis[v]){
            dfs(start, v, d + 1);
        }

    }
    nodes.pop_back();
    vis[u] = 0;
}

int main(){
    FASTER;

    int n,m;
    cin >> n >> m;

    G.assign(n + 1 , vi());

    deg.assign(n + 1 ,0);
    for (int i = 0; i < m; ++i) {
        int a,b;
        cin >> a >> b;
        deg[a]++;
        deg[b]++;
        G[a].push_back(b);
        G[b].push_back(a);
        adj[a][b] = adj[b][a] = 1;
    }

    int res = INF;


    for (int i = 1; i <= n; ++i) {
//      printf("start = %d\n", i);
        vis.assign(n + 1 ,0);
        ans = INF;
        nodes.clear();
        dfs(i,i, 1);
        res = min(ans, res);
    }

    if(res == INF)
        cout << -1 << endl;
    else
        cout << res << endl;

    return 0;
}

