void sort1(int temp[100],int n)
{
    int i,j,temp1;
    for(i=0;i<n-1;i++)
    {
        for(j=i+1;j<n;j++)
        {
            if(temp[i]<temp[j])
            {
                temp1=temp[j];
                temp[j]=temp[i];
                temp[i]=temp1;
            }
        }
    }
}
int main()
{
    int n,a,i,sum=0;
    int temp[100];
    scanf("%d",&n);
    scanf("%d",&a);
    for(i=0;i<n-1;i++)
    {
        scanf("%d",&temp[i]);
    }
    sort1(temp,n-1);

    if(a>temp[0])
    {
        printf("0");
        return 0;
    }
    else
    {
        while(a<=temp[0])
        {
            a+=1;
            temp[0]-=1;
            sum+=1;
            sort1(temp,n-1);
        }
        printf("%d",sum);
        return 0;
    }
