#define getcx getchar
inline void in(int &n)
{
    n=0; int ch = getcx(); int sign = 1;
    while(ch < '0' || ch > '9') { if(ch == '-') sign=-1; ch = getcx(); }
    while(ch >= '0' && ch <= '9') { n = (n << 3) + (n << 1) + ch - '0', ch = getcx(); }
    n = n * sign;
}
set<int> S[100005];
int main()
{
    int n,m;
    in(n);
    in(m);

    for(int i=1; i<=m; i++)
    {
        int x,y;
        in(x);
        in(y);

        S[x].insert(y);
        S[y].insert(x);

    }

    set<int>::iterator it,it2;

    int ans=INF;

    for(int i=1; i<=n; i++)
    {
        for(int j=i+1; j<=n; j++)
        {
            if(S[i].find(j)==S[i].end())//i and j are not friends
            {
                continue;
            }
            //i and j are friends
            for(it=S[j].begin(); it!=S[j].end(); it++)
            {
                it2=S[i].find(*it);
                if(it2!=S[i].end())
                {
                    int k=*it2;
                    int val=S[i].size()+S[j].size()+S[k].size();
                    val-=6;

                    ans=min(ans,val);
                }
            }
        }
    }
    if(ans==INF)
        printf("-1\n");
    else
        printf("%d\n",ans);
    return 0;
}

